package Inteiros;

public class Arredondamento {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 10;
		int y = 20;
		int z;
		
		
		z = y += x / 5;	
		
		
		System.out.println("Números : " + x + y + z);
		
		System.out.println("Números : " + (x) + (y) + (z));
		
	}

}
